namespace Flame\Diagnostics\ServicePanel;

use Nette,
	Nette\Diagnostics\Debugger;

class ServicePanel extends \Nette\Object implements \Nette\Diagnostics\IBarPanel
{

	/**
	 * @var \Nette\DI\Container
	 */
	private $container;

	/***
	 * @param \Nette\DI\Container $container
	 */
	public function __construct(\Nette\DI\Container $container)
	{
		$this->container = $container;
	}

	/**
	 * @return string
	 */
	public function getTab()
	{
		return '<span title="Shows list of all available services">'
			. '<img src="data:image/png;base64," alt="icon">'
			. 'Services'
			. '</span>';
	}

	/**
	 * @return array
	 */
	private function getList()
	{
		$annotations = $this->container->getReflection()->getAnnotations();

		$list = array();

		foreach ($annotations['property'] as $annotation) {
			list($class, $name) = explode(' $', $annotation);

			if ($class === 'Nette\DI\NestedAccessor')
				continue;

			$namespace = NULL;

			if (Nette\Utils\Strings::contains($name, '_'))
				list($namespace) = explode('_', $name);

			if (!isset($list[$namespace]))
				$list[$namespace] = array();

			$list[$namespace][] = array(
				'name' => $name,
				'class' => $class
			);
		}

		return $list;
	}

	/**
	 * @return string
	 */
	public function getPanel()
	{
		ob_start();
		$list = $this->getList();
		require_once __DIR__ . "/templates/bar.service.panel.phtml";
		return ob_get_clean();
	}

	/**
	 * @param \Nette\DI\Container $container
	 * @return Panel
	 */
	public static function register(\Nette\DI\Container $container)
	{
		$panel = new static($container);
		if (Debugger::$bar) {
			Debugger::$bar->addPanel($panel);
		}
		return $panel;
	}

}